step.params:
try {
    var t = value.match(/(\d+)d (\d+)h(\d+)m(\d+)s/);
    return t[1] * 86400 + t[2] * 3600 + t[3] * 60 + t[4] * 1;
}
catch (error) {
    throw "HAProxy uptime is not found : " + error;
}

try {
    return value.match(/HAProxy version (\d+\.\d+\.\d+),/)[1];
}
catch (error) {
    throw "HAProxy version is not found : " + error;
}
