step.params:
var newvalue;
switch(value) {
  case "unknown":
    newvalue = 0;
    break;
  case "notpresent":
    newvalue = 1;
    break;
  case "down":
    newvalue = 2;
    break;
  case "lowerlayerdown":
    newvalue = 3;
    break;
  case "testing":
    newvalue = 4;
    break;
  case "dormant":
    newvalue = 5;
    break;
  case "up":
    newvalue = 6;
    break;  default:
    newvalue = "Problem parsing interface operstate in JS";
}
return newvalue;

//count vfs.fs.inode.pfree
var inode_free;
var inode_total;
JSON.parse(value).forEach(function(metric) {
  if (metric['name'] == 'node_filesystem_files'){
      inode_total = metric['value'];
  } else if (metric['name'] == 'node_filesystem_files_free'){
      inode_free = metric['value'];
  }
});
return (inode_free/inode_total)*100;

