		$schema = ['type' => XML_ARRAY, 'rules' => [
			'constants' => ['type' => XML_INDEXED_ARRAY, 'prefix' => 'constant', 'rules' => [
				'constant' => ['type' => XML_ARRAY, 'rules' => [
					'first' => ['type' => XML_STRING, 'in' => ['0' => 'ZERO']]
				]]
			]]
		]];
		$source = [
			'zabbix_export' => [
				'constants' => [
					'constant' => [
						'first' => '1'
					]
				]
			]
		];
		$source = (new CConstantImportConverter($schema))->convert($source);
	}

